mapper.select:
SELECT COUNT(*)
FROM boards
WHERE boardwriter=#{userid}

SELECT rnum, boardno, boardtitle, boarddate, boardwriter
FROM (
  SELECT ROWNUM as rnum, boardno, boardtitle, boarddate, boardwriter
  FROM (
    SELECT boardno, boardtitle, boarddate, boardwriter 
    FROM boards
    ORDER BY boardno DESC
  )
  WHERE rownum <= #{endRowNo}
)
WHERE rnum >= #{startRowNo}

SELECT rnum, boardno, boardtitle, boarddate, boardwriter
FROM (
	SELECT ROWNUM as rnum, boardno, boardtitle, boarddate, boardwriter
	FROM (
		SELECT boardno, boardtitle, boarddate, boardwriter
		FROM boards
		WHERE boardwriter=#{userid}
		ORDER BY boardno DESC
	)
	WHERE rownum <= #{pager.endRowNo}
)
WHERE rnum >= #{pager.startRowNo}

mapper.update:
UPDATE boards
SET boardtitle=#{boardtitle}, boardcontent=#{boardcontent}
WHERE boardno=#{boardno}
