mapper.select:
SELECT rnum, commentno, boardno, commentcontent, commentdate, commentwriter
	FROM (
	  SELECT ROWNUM as rnum, commentno, boardno, commentcontent, commentdate, commentwriter
	  FROM (
	    SELECT commentno, boardno, commentcontent, commentdate, commentwriter
	    FROM comments
	    ORDER BY commentno DESC
	  )
	  WHERE rownum <= #{endRowNo} 
	  	AND boardno=#{boardno}
	)
WHERE rnum >= #{startRowNo}
	AND boardno=#{boardno}

mapper.insert:

	select SEQ_COMMNO.nextval from dual

INSERT INTO comments(
	commentno, boardno, commentcontent, commentdate, commentwriter
)
VALUES(
	#{commentno}, #{boardno}, #{commentcontent}
	, #{commentdate}, #{commentwriter}
)

mapper.update:
UPDATE comments
SET
	commentcontent=#{commentcontent}
WHERE commentno=#{commentno}
