mapper.select:


SELECT rnum, denno, dendomain, denname, denaddress
	FROM (
	  SELECT ROWNUM as rnum, denno, dendomain, denname, denaddress
	  FROM (
	    SELECT denno, dendomain, denname, denaddress 
	    FROM dentists
	    WHERE denname like '%' || #{denname} || '%'
	    ORDER BY denname DESC
	  )
	  WHERE rownum <= #{pager.endRowNo} 
	)
WHERE rnum >= #{pager.startRowNo}

SELECT denno, dendomain, denname, denaddress
FROM dentists
WHERE denno=#{denno}

SELECT denno, dendomain, denname, denaddress
FROM dentists
WHERE dendomain=#{dendomain}

mapper.insert:

	select DENNO_SEQ.nextval from dual

INSERT INTO dentists(
	denno, dendomain, denname, denaddress
)
VALUES(
	#{denno}, #{dendomain}, #{denname}, #{denaddress}
)
